declare const styles: {
    root: {
        position: "absolute";
        bottom: number;
        width: string;
        height: number;
        backgroundColor: string;
    };
    rootExpanded: {
        height: string;
    };
    titleWrapper: {
        paddingVertical: number;
        paddingHorizontal: number;
        flexDirection: "row";
    };
    title: {
        flex: number;
        fontSize: number;
    };
    buttonsRow: {
        flexDirection: "row";
        paddingHorizontal: number;
    };
};
export default styles;
